import * as React from "react";
import { List, Datagrid, TextField, ReferenceField, EditButton, ShowButton, ChipField } from 'react-admin';


import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
    price: { color: 'red',
             background: 'black'    },

});

const TitleField = props => {
    const classes = useStyles();
    return <TextField className={classes.price} {...props} />;
};




export const PostList = props =>{
    const classes = useStyles();
   return (
    <List {...props}>
        <Datagrid className>
            <TextField className={classes.price} source="id"/>
            <ReferenceField label="user" source="userId" reference="users">
                <ChipField source="name"/>
            </ReferenceField>
            <TitleField source="title"/>
            <TextField headerClassName={classes.price}  source="body"/>
            <EditButton/>
            <ShowButton/>
        </Datagrid>
    </List> 
)};
